function initUser (mongoose) {
    
    var userSchema = new mongoose.Schema({
        //_id: (autogenerated)
        
        email: String,
        username: String,
        password: String,
        
        playerName: String,
        nickName: String,
        status: String,
        role: String,
        
        hasPaid: Boolean,
        
        active: Boolean,
        deleted: Boolean
    });
    
    return userSchema;
}

module.exports.initUser = initUser;